import { useState, useEffect } from "react";
import {Nav} from 'react-bootstrap';
import axios from 'axios'

function TabContent(props){
  let cList2 = props.tabContList[props.tab]
  return <p>{cList2.map((c)=>{return (<p>{c}</p>)})}</p>
}

function Tab({tabList, tabContList}) {
  let [tab, setTab] = useState(0);
  
  return (
  <div><Nav className="mt-5 mb-3" variant="tabs" defaultActiveKey="link-0">
      {tabList.map((t, idx)=> {
          let k = "link-" + idx;
          return (    
          <Nav.Item>
          <Nav.Link eventKey={k} onClick={()=>{setTab(idx); fetchData}}>{t}</Nav.Link>
          </Nav.Item>
          )
      }
      )}
  </Nav>
  <TabContent tab={tab} tabContList={tabContList}/>
  </div>
)
}


export default function Article_category() {
    let [getData, setGetData] = useState(0);
    
  useEffect(() => {
    async function fetchData() {
      const result = await axios.get(
        "/article/" + "총칙"+"/" + "1"
      );
       console.log(result.data);
       setGetData(result.data);
    }
    fetchData();
  }, [])

  return (
    <div>
        {(typeof getData.c_list2 === 'undefined') ? (
      // fetch가 완료되지 않았을 경우에 대한 처리
      <p>loding...</p>
      ) : (<div>
            <Tab tabList={getData.c_list1} tabContList={getData.c_list2}/>
              {getData.a_dic.name_list}
            </div>
        )}
    </div>
  )
}
