import React from 'react';
import Header from './header.js';
import { useState, useEffect } from 'react';
import Pagination from "react-js-pagination";

function Table({list, total}) {
        let [curList, setCurList] = useState([]);
        const [page, setPage] = useState(1);
    console.log("search conts, list", list)
    useEffect(() => {
        //kind가 list이면 즉 더보기 버튼을 클릭하기 전이면 3개만, 더보기 버튼을 클릭 후일때는 10개씩
        setCurList(list.slice((page-1)*10, page*10))

      }, [])
    console.log(curList)
      const Paging = () => {
        const handlePageChange = (page) => {
          setPage(page);
          
          setCurList(list.slice((page-1)*10, page*10))
          console.log("page, curList:", curList)
          //type === "search" &&(window.location.href = "/search/" + kind + "/" + word + "/" + option + "/" + page)
        };
      
        return (
          <div>
          <Pagination
            activePage={page} // 현재 페이지
            itemsCountPerPage={10} // 한 페이지랑 보여줄 아이템 갯수
            totalItemsCount={total} // 총 아이템 갯수
            pageRangeDisplayed={5} // paginator의 페이지 범위
            prevPageText={"‹"} // "이전"을 나타낼 텍스트
            nextPageText={"›"} // "다음"을 나타낼 텍스트
            onChange={handlePageChange} // 페이지 변경을 핸들링하는 함수
          />
          </div>
        );
      };
    
    function change(obj) {
        let id = obj.id;
        let l = list[id];
        let str  = obj.innerText;
        alert(obj.id);
        if (str.length < l[0].length +2 ) {
            str = '<i class="bi bi-caret-down"></i>' + str
            alert(str);
            for (let i = 0; i < l[1].length; i++) {
                str += '<br>' + l[1][i];
            }
        }
        else {
            str = '<i class="bi bi-caret-right"></i>' + l[0];
        }
        obj.innerHTML = str;
    }
    
    return (
        <div class="div2">
            <table class="table table-hover">
                <thead><tr><th id="t1"><span className="fontFt">번호</span></th><th><span className="fontFt">조목</span></th></tr></thead>
                <tbody>
            {curList.map((string, idx) => {
                return(    
                    <tr>
                        <td><span className="fontFt">{(page-1)*10+idx}</span></td>
                        <td id={idx} onClick={change}><span className="fontFt"><i class="bi bi-caret-down"></i>{string[0]}</span></td>
                    </tr>
                )
            })}
            </tbody>
            </table>
            <Paging/>
        </div>
    )
      
}


export default Table;
