import React from 'react';


function Search_box() {
  // form 가져오기
 let opt = document.getElementById("option");
 let query = document.getElementById("query");
 let formData = new FormData();
 formData.append("opt", opt);
 formData.append("query", query);

 const SearchUp = (e) => {
  e.preventDefault();
  
  fetch("/seach", {
    method: "GET",
    headers: {
      "Content-Type": "multipart/form-data",
    },
    body: new FormData(formData),
  })
    .then((response) => {
      if (response.ok === true) {
        return response.json();
      }
      throw new Error("에러 발생!");
    })
    .catch((error) => {
      alert(error);
    })
    .then((data) => {
      console.log(data);
    });
  };
    return(
          <div className="Search_box">
          <form name="sform" onSubmit={SearchUp} method="get" id="search">
              <select name="option" id="option">
                  <option value="1" className="opt">통합검색</option>
                  <option value="2" className="opt">민법</option>
                  <option value="3" className="opt">판례</option>
              </select>
              <input id="query" name="query" type="text" placeholder="검색어를 입력해 주세요"></input>
              <button id="icon" type="submit"><i className="bi bi-search"></i></button>
          </form>
          </div>
          
          
    )
  
}







