import React from 'react';
import { useState, useEffect } from 'react';


function Search_box({opt}) {
    const [query, setQuery] = useState("");
    let [option, setOption] = useState("");
    const onSubmit = async () => {
      
      opt === 'undifined' && (setOption(opt))
      window.location.href = "/search/" + query + "/" + option;
    };

    const onOptChange = (e) => {
      setOption(e.target.value);
      console.log(option);
    };
    const optList = ['통합검색', '민법', '판례'];
    return (
     <div>
      <select name="option" id="option" onChange={onOptChange}>
      {optList.map((o, idx) => {
        return(  
          String(idx) === String(opt) ? (
            <option value={idx+1} class="opt" selected>{ o }</option>
            ) : (
              <option value={idx+1} class="opt">{ o }</option>
            )
        )
    })}
      </select>
      {
        query === "" ? (
          <input id="query" type="text" placeholder="검색어를 입력해 주세요"
      onChange={(e) => {
      setQuery(e.target.value);
      console.log(query);
      }}></input>
        ) : (
          <input id="query" type="text"
      onChange={(e) => {
      setQuery(e.target.value);
      console.log(query);
      }} value={query}></input>
        )
      }
      
  
      <button id="icon"
       type="button"
       onClick={() => {
       onSubmit();
       }}><i className="bi bi-search"></i></button>
     </div>
    );
  }
  



