import React from 'react';
import { useState, useEffect } from 'react';


function Search_box() {
    const [word, setWord] = useState("");
    let [option, setOption] = useState("");
    const onSubmit = async () => {
      window.location.href = "/search/" + word + "/" + option;
    };

    const onOptChange = (e) => {
      setOption(e.target.value);
      console.log(option);
    };
    option = 1
    return (
     <div>
      <select name="option" id="option" onChange={onOptChange}>
                <option value="1" class="opt">통합검색</option>
                <option value="2" class="opt">민법</option>
                <option value="3" class="opt">판례</option>
      </select>
      <input id="query" type="text" placeholder="검색어를 입력해 주세요"
      onChange={(e) => {
      setWord(e.target.value);
      console.log(word);
      }}></input>
  
      <button id="icon"
       type="button"
       onClick={() => {
       onSubmit();
       }}><i className="bi bi-search"></i></button>
     </div>
    );
  }
  



