import { useState } from "react";
import {Nav} from 'react-bootstrap';

function TabContent(props){
    if(props.tab === 0){
        console.log("props.tabContList" , props.tabContList)
      return <div>Tab 1 내용입니다.</div>
    }else if(props.tab === 1) {
      return <div>Tab 2 내용입니다.</div>
    }else if(props.tab === 2){
      return <div>Tab 3 내용입니다.</div>
    }
  }

export default function Tab({tabList, tabContList}) {
    let [tab, setTab] = useState(0);
    
    {(typeof tabContList === 'undefined') ? (
        // fetch가 완료되지 않았을 경우에 대한 처리
        <p>loding...</p>
        ) : (<p>{tabContList}</p>)}
    console.log("tabContList" , tabContList)
  return (
    <div><Nav className="mt-5 mb-3" variant="tabs" defaultActiveKey="link-0">
        {tabList.map((t, idx)=> {
            let k = "link-" + idx;
            return (    
            <Nav.Item>
            <Nav.Link eventKey={k} onClick={()=>{setTab(idx)}}>{t}</Nav.Link>
            </Nav.Item>
            )
        }
        )}
  </Nav>
    <TabContent tab={tab} tabContList={tabContList}/>
    </div>
  )
}
