import React from 'react';
import Header from './header.js';
import { useState, useEffect } from 'react';

import Paging from '../elements/page.js';
function Search_conts({list, word, page, total, kind, option}) {
    let curList = []
    console.log("search conts, kind", kind)
    console.log("search conts, word", word)
    console.log("search conts, option", option)
    
    useEffect(() => 
    {    
        
        console.log("search conts, list", list)
        //kind가 list이면 즉 더보기 버튼을 클릭하기 전이면 3개만, 더보기 버튼을 클릭 후일때는 10개씩
        {kind ==="alist" || kind ==="plist" ? (curList = list.slice(0, 3)) : (curList = list.slice((page-1)*10, page*10))}
        console.log(curList)
    }, [])
    const btnList = {
        alist: <p>{total > 3 && (<button type="button" onClick={() => {
            window.location.href = "/search/" + "article" + "/" + word + "/" + option ;}}>더보기</button>)}</p>,
        plist: <p>{total > 3 && (<button type="button" onClick={() => {
            window.location.href = "/search/" + "precedent" + "/" + word + "/" + option ;}}>더보기</button>)}</p>,
        article: <p><Paging p={page} kind={kind} total={total} type="search" word={word} option={option}/></p>,
        precedent: <p><Paging p={page} kind={kind} total={total} type="search" word={word} option={option}/></p>
    }
    
    console.log("search conts, page", page);
    curList.map((string) => {
        return(    
        <li class="left">
        {
            string.map((str, idx) => {
               return (
                <p>
                {idx === 1 ? (
                str.map((s) => {
                    return(
                        s === word ? (
                            null
                        ) : (
                            console.log(s)
                        )
                    )
                })
                ) : ( null
                )}
                </p>
               )
            })
        }
        </li>
        )
    })

    const onSubmit = async () => {
        window.location.href = "/search/" + kind + "/" + word + "/" + option 
    };
    return (
        <div class="div2">
            <ul>
            {curList.map((string) => {
                return(    
                <li class="left">
                {
                    string.map((str, idx) => {
                       return (
                        <p>
                        {idx === 0 ? (
                        str.map((s) => {
                            return(
                                s === word ? (
                                    <span class = "div2-title bold">{ s }</span>
                                ) : (
                                    <span class = "div2-title">{ s }</span>
                                )
                            )
                        })
                        ) : (
                            idx === 1 && (
                                str.map((s) => {
                                    return(
                                        s === word ? (
                                            <span class = "div2-cont bold">{ s }</span>
                                        ) : (
                                            <span class = "div2-cont">{ s }</span>
                                        )
                                        
                                    )
                                })
                            )
                        )}
                        </p>
                       )
                    })
                }
                </li>
                )
            })}
            </ul>
            {btnList[kind]}
        </div>
    )
      
}


export default Search_conts;
