import { useState, useEffect } from 'react';
import axios from 'axios'
import { useParams } from "react-router";
import Search_box from './search_box.js';
import Menubar from '../elements/menubar';
import Search_title from './search_titlle.js'
import Search_conts from './search_conts.js';
import Paging from '../elements/page.js';

function Search() {
  const [searchData, setSearchData] = useState([]);
  const {query, option} = useParams();
  console.log(query);
  console.log(option);
  useEffect(() => {
    async function fetchData() {
      const result = await axios.get(
        "/search?query=" + query + "&option=" + option
      );
       console.log(result.data);
       setSearchData(result.data);
    }
    fetchData();
  }, [])
  
  return (
    <div>
    <header id="header">
      <p id="logo">민법을 적용하는 법</p>
      <Menubar/>
      </header>
    <nav id="nav">&nbsp;</nav>
    <div id="section">
      <Search_box/>
        
      { (typeof searchData.a_dic === 'undefined') ? (
      // fetch가 완료되지 않았을 경우에 대한 처리
      <p>loding...</p>
      ) : (
      <div id="div">
        <Search_title num={searchData.a_dic.total}/>
        {option != 3  ? (<Search_conts list={searchData.a_dic.name_list} word={searchData.a_dic.word} page={searchData.a_dic.page}/>)
        : (<p>검색 결과가 없습니다.</p>)}
        <Search_title num={searchData.p_dic.total}/>
        {option != 2  ? (<Search_conts list={searchData.a_dic.name_list} word={searchData.a_dic.word} page={searchData.a_dic.page}/>)
        : (<p>검색 결과가 없습니다.</p>)}
        <Paging />
      </div>
      )}
    </div>
    <aside id="aside">&nbsp;</aside>
    <footer></footer>
  </div>
    );

};
