import React from 'react';
import Header from './header.js';
import { useState, useEffect } from 'react';
import Pagination from "react-js-pagination";

function Table({list, total}) {
    let [totalList, setTotalList] = useState([]);//현재 페이지에 맞는 리스트
    let [curList, setCurList] = useState([]); //클릭됐는지(check) 여부에 따라 달라지는 리스트
    const [page, setPage] = useState(1);
    let [check, setCheck] = useState(0);
    console.log("search conts, list", list)
    useEffect(() => {
        //kind가 list이면 즉 더보기 버튼을 클릭하기 전이면 3개만, 더보기 버튼을 클릭 후일때는 10개씩
        let l = list.slice((page-1)*10, page*10);
        let new_l = []
        for (let i=0; i < l.length; i++) {
          new_l.push([l[i][0]])
        }
        setTotalList(l)
        setCurList(new_l)
      }, [])
    console.log(curList)
      const Paging = () => {
        const handlePageChange = (page) => {
          setPage(page);
          let l = list.slice((page-1)*10, page*10);
          let new_l = []
          for (let i=0; i < l.length; i++) {
            new_l.push([l[i][0]])
          }
          setTotalList(l)
          setCurList(new_l)
          console.log("page, curList:", curList)
          //type === "search" &&(window.location.href = "/search/" + kind + "/" + word + "/" + option + "/" + page)
        };
      
        return (
          <div>
          <Pagination
            activePage={page} // 현재 페이지
            itemsCountPerPage={10} // 한 페이지랑 보여줄 아이템 갯수
            totalItemsCount={total} // 총 아이템 갯수
            pageRangeDisplayed={5} // paginator의 페이지 범위
            prevPageText={"‹"} // "이전"을 나타낼 텍스트
            nextPageText={"›"} // "다음"을 나타낼 텍스트
            onChange={handlePageChange} // 페이지 변경을 핸들링하는 함수
          />
          </div>
        );
      };

    const handleContChange = (obj) => {
        console.log(obj.innerText)
        let new_l = []
        if (check == 0) {
          for (let i=0; i < totalList.length; i++) {
            let l = []
            l.push(totalList[i][0])
            for (let k=0; k < totalList[i][1].length; k++){
              l.push(totalList[i][1][k])
            }
            new_l.push(l)
          }
          setCheck(1)
        }
        else {
          for (let i=0; i < totalList.length; i++) {
            new_l.push([totalList[i][0]])
          }
        }
        setCurList(new_l)
        console.log("page, curList:", curList)
        //type === "search" &&(window.location.href = "/search/" + kind + "/" + word + "/" + option + "/" + page)
    };
    return (
        <div class="div2">
            <table class="table table-hover">
                <thead><tr><th id="t1"><span className="fontFt">번호</span></th><th><span className="fontFt">조목</span></th></tr></thead>
                <tbody>
            {curList.map((string, idx) => {
                return(    
                    <tr>
                        <td><span className="fontFt">{(page-1)*10+idx}</span></td>
                        <td id={idx} onClick={handleContChange}><span className="fontFt"><i class="bi bi-caret-down"></i>
                          {string.map((str)=> {return <p>{str}</p>})}
                        </span></td>
                    </tr>
                )
            })}
            </tbody>
            </table>
            <Paging/>
        </div>
    )
      
}


export default Table;
